mapper.insert:
insert into board(
    nickname,
    password,
    content
)values(
    #{nickname},
    #{password},
    #{content}
)

    SELECT LAST_INSERT_ID()

mapper.select:
select count(*) from board
where id = #{id}
and password = #{password}
and is_deleted = false

mapper.update:
update board
set is_deleted = true
where id = #{id}
